import { useEffect, useState, useContext, useRef } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";

export default function Canvasinit({post, image_path }) {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const [drawing, setDrawing] = useState(false);
    const canvasRef = useRef(null);
    const ctxRef = useRef(null);
    const { user } = useContext(AuthContext);
    useEffect(() => {
        // console.log(anno_arr)
        const c = document.getElementById(image_path);
        const ctx = c.getContext("2d");
        var imageObj1 = new Image();
        imageObj1.src = PF + image_path;
        imageObj1.onload = function () {
            ctx.drawImage(imageObj1, 0, 0);
            if(post.user_id===user.id && post.annotation_path!==""){
                const anno_arr = post.annotation_path.split("/") // "/279,84/153,97"
                for(const xy of anno_arr){
                    const xy_arr = xy.split(',')
                    ctx.strokeRect(parseInt(xy_arr[0]), parseInt(xy_arr[1]), 60, 80);
                }
            }
        };
    }, [image_path, PF]);

    useEffect(() => {
        const canvas = canvasRef.current;
        // For supporting computers with higher screen densities, we double the screen density
        // canvas.width = window.innerWidth * 2;
        // canvas.height = window.innerHeight * 2;
        // canvas.style.width = `1000`;
        // canvas.style.height = `500`;
        // Setting the context to enable us draw
        const ctx = canvas.getContext('2d');
        // ctx.scale(2, 2);
        ctx.lineCap = 'round';
        ctx.strokeStyle = 'red';
        ctx.lineWidth = 10;
        ctxRef.current = ctx;
      }, [image_path]);

    const addPoint = () => {
        const ctx = document.getElementById(image_path).getContext("2d");
        ctx.beginPath();
        ctx.lineWidth = "10";
        ctx.strokeStyle = "blue";
        ctx.rect(50, 50, 150, 80);
        // ctx.stroke();
        // var imageObj1 = new Image();
        // imageObj1.src = PF + image_path;
        // imageObj1.onload = function () {
            // ctx.drawImage(imageObj1,0,0);
    };

    // const handleCursorMovement = (event) => {
    //     let rect = event.target.getBoundingClientRect();
    //     let x = event.clientX - rect.left
    //     let y =  event.clientY - rect.top
    //     const ctx = document.getElementById(image_path).getContext("2d");
    //     ctx?.strokeRect(x, y, 40, 50);
    // }

    const startDraw = ({ nativeEvent }) => {
        const { offsetX, offsetY } = nativeEvent;
        ctxRef.current.beginPath();
        ctxRef.current.moveTo(offsetX, offsetY);
        setDrawing(true);
      };
    const stopDraw = async ({ nativeEvent }) => {
        ctxRef.current.closePath();
        if (!drawing) return;
        const { offsetX, offsetY } = nativeEvent;
        // ctxRef.current.lineTo(offsetX, offsetY);
        ctxRef.current.strokeRect(offsetX, offsetY, 60, 80);
        const newXY = {
            x: offsetX.toString(),
            y: offsetY.toString()
        }
        try {
            await axios.put(`/posts/update/${post.id}`, newXY);
            // window.location.reload();
        } catch (err) {}
        console.log(offsetX, offsetY)
        setDrawing(false);
      };
      const clear = () => {
        ctxRef.current.clearRect(
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );
      };
    return (
        <div>
            <canvas
                ref={canvasRef}
                id={image_path}
                width="1000"
                height="300"
                style={{ border: "1px solid #d3d3d3" }}
                onMouseDown={startDraw}
                onMouseUp={stopDraw}
                // onClick={handleCursorMovement()}
            >
                Your browser does not support the HTML canvas tag.
            </canvas>
            {/* <canvas
                onMouseDown={startDraw}
                onMouseUp={stopDraw}
                onMouseMove={draw}
                ref={canvasRef}
            /> */}
            <br />
            <button onClick={() => clear()}>Clear</button>
        </div>
    );
}
