import { useEffect, useState, useContext } from "react";

export default function Canvasinit({ image_path }) {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    useEffect(() => {
        const c = document.getElementById(image_path);
        const ctx = c.getContext("2d");
        var imageObj1 = new Image();
        imageObj1.src = PF + image_path;
        imageObj1.onload = function () {
            ctx.drawImage(imageObj1, 0, 0);
        };
    }, [image_path]);

    const addPoint = () => {
        const ctx = document.getElementById(image_path).getContext("2d");
        ctx.beginPath();
        ctx.lineWidth = "10";
        ctx.strokeStyle = "blue";
        ctx.rect(50, 50, 150, 80);
        ctx.stroke();
        // var imageObj1 = new Image();
        // imageObj1.src = PF + image_path;
        // imageObj1.onload = function () {
            // ctx.drawImage(imageObj1,0,0);
    };

    const handleCursorMovement = (event) => {
        let rect = event.target.getBoundingClientRect();
        let x = event.clientX - rect.left
        let y =  event.clientY - rect.top
        const ctx = document.getElementById(image_path).getContext("2d");
        ctx?.strokeRect(x, y, 40, 50);
    }
    return (
        <div>
            <canvas
                id={image_path}
                width="1000"
                height="300"
                style={{ border: "1px solid #d3d3d3" }}
                onClick={handleCursorMovement()}
            >
                Your browser does not support the HTML canvas tag.
            </canvas>
            <br />
            <button onClick={() => addPoint()}>Add Point</button>
        </div>
    );
}
